CREATE TABLE IF NOT EXISTS node (id INT (10) UNSIGNED  NOT NULL, lat FLOAT (10,7) NOT NULL, lon FLOAT (10,7) NOT NULL, PRIMARY KEY (id) );
CREATE TABLE IF NOT EXISTS way (id INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS relation (id INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS nd (id INT (10) UNSIGNED  NOT NULL,id_way INT (10) UNSIGNED  NOT NULL,id_node INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_way) REFERENCES way(id),FOREIGN KEY (id_node) REFERENCES node(id));
CREATE TABLE IF NOT EXISTS tag_key (id INT (10) UNSIGNED  NOT NULL,k VARCHAR(25)  NOT NULL,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS uncertain_key (id INT (10) UNSIGNED  NOT NULL,k VARCHAR(25)  NOT NULL,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS tag_value (id INT (10) UNSIGNED  NOT NULL,v VARCHAR(255)  NOT NULL,id_tag_key INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_tag_key) REFERENCES tag_key(id));
CREATE TABLE IF NOT EXISTS uncertain_value (id INT (10) UNSIGNED  NOT NULL,v VARCHAR(255)  NOT NULL,id_tag_key INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_tag_key) REFERENCES uncertain_key(id));
CREATE TABLE IF NOT EXISTS node_tag (id INT (10) UNSIGNED  NOT NULL,id_node INT (10) UNSIGNED  NOT NULL,id_tag INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_node) REFERENCES node(id),FOREIGN KEY (id_tag) REFERENCES tag_value(id));
CREATE TABLE IF NOT EXISTS way_tag (id INT (10) UNSIGNED  NOT NULL,id_way INT (10) UNSIGNED  NOT NULL,id_tag INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_way) REFERENCES way(id),FOREIGN KEY (id_tag) REFERENCES tag_value(id));
CREATE TABLE IF NOT EXISTS relation_tag (id INT (10) UNSIGNED  NOT NULL,id_relation INT (10) UNSIGNED  NOT NULL,id_tag INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_relation) REFERENCES relation(id),FOREIGN KEY (id_tag) REFERENCES tag_value(id));
CREATE TABLE IF NOT EXISTS role (id INT (10) UNSIGNED  NOT NULL,v VARCHAR(25) NOT NULL,PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS member_node (id INT (10) UNSIGNED  NOT NULL,id_node INT (10) UNSIGNED  NOT NULL,id_relation INT (10) UNSIGNED  NOT NULL,id_role INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_relation) REFERENCES relation(id),FOREIGN KEY (id_role) REFERENCES role(id));
CREATE TABLE IF NOT EXISTS member_way (id INT (10) UNSIGNED  NOT NULL,id_way INT (10) UNSIGNED  NOT NULL,id_relation INT (10) UNSIGNED  NOT NULL,id_role INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_relation) REFERENCES relation(id),FOREIGN KEY (id_role) REFERENCES role(id));
CREATE TABLE IF NOT EXISTS member_relation (id INT (10) UNSIGNED  NOT NULL,id_rel INT (10) UNSIGNED  NOT NULL,id_relation INT (10) UNSIGNED  NOT NULL,id_role INT (10) UNSIGNED  NOT NULL,PRIMARY KEY (id),FOREIGN KEY (id_relation) REFERENCES relation(id),FOREIGN KEY (id_role) REFERENCES role(id));
